<template><div><div class="line line-dashed b-b line-lg"></div><div class="form-group"><div class="col-sm-12 btn-toolbar"><a class="btn btn-info" @click="addGoods">{{$t("添加商品")}}</a><a class="btn btn-info btn-goods-import">{{$t("商品导入")}}<input type="file" accept="application/vnd.ms-excel,application/vnd.openxmlformats-officedocument.spreadsheetml.sheet" /></a><a class="btn btn-info" @click="formExport">{{$t("表格下载")}}</a></div></div><div class="line line-dashed b-b line-lg"></div><div class="form-group"><div class="col-sm-8"><a class="btn btn-info" @click="removeGoods">{{$t("批量删除")}}</a></div></div><div class="col-sm-12"><grids-table :query-adaptor="refreshGoodsList" _key="mpId" auto-read><template scope="item" slot="body"><grid-column :scope="item" _checkbox _ref="checked" :checked-list="removingList"></grid-column><grid-column :scope="item" :header="$t('商品名称')" _ref="mpName" :style="{ 'max-width': '100px' }"></grid-column><grid-column :scope="item" :header="$t('商品编码')" _ref="code" :style="{ 'max-width': '100px' }"></grid-column><grid-column :scope="item" :header="$t('商品分类')" _ref="categoryName" :style="{ 'max-width': '100px' }"></grid-column><grid-column :scope="item" :header="$t('优先级')"><grid-act-input :scope="item" _number _ref="sort" @change="resort"></grid-act-input></grid-column><grid-column :scope="item" :header="$t('商家名称')" _ref="merchantName" :style="{ 'max-width': '100px' }"></grid-column><grid-column :scope="item" :header="$t('渠道')" _ref="channel" :style="{ 'max-width': '100px' }"></grid-column><grid-column :scope="item" :header="$t('操作')"><grid-act-click :scope="item" :label="$t('编辑')" @click="editItem"></grid-act-click><grid-act-click :scope="item" :label="$t('删除')" clazz="btn-danger" @click="removeItem"></grid-act-click></grid-column></template><template scope="info" slot="foot"><grid-pagination :scope="info" slot="foot"></grid-pagination></template></grids-table></div></div></template>